import textio.TextIO;
public class CostOfWidgets {
    public static void main(String[] args) {
        int numberOfWidgets;
        double costPerWidget;
        double totalCost;
        System.out.print("How many widgets do you want? ");
        numberOfWidgets = TextIO.getlnInt();
        System.out.print("How much does each widget cost? ");
        costPerWidget = TextIO.getlnDouble();
        totalCost = numberOfWidgets * costPerWidget;
        System.out.printf("The total cost is $%1.2f%n", totalCost);
    }
}

import java.util.Scanner;
public class CostOfWidgets {
    public static void main(String[] args) {
        int numberOfWidgets;
        double costPerWidget;
        double totalCost;
        Scanner in = new Scanner(System.in);
        System.out.print("How many widgets do you want? ");
        numberOfWidgets = in.nextInt();
        System.out.print("How much does each widget cost? ");
        costPerWidget = in.nextDouble();
        totalCost = numberOfWidgets * costPerWidget;
        System.out.printf("The total cost is $%1.2f%n", totalCost);
    }
}
