import javafx.application.Application;
import javafx.scene.layout.BorderPane;
import javafx.scene.Scene;
import javafx.stage.Stage;
import javafx.scene.canvas.Canvas;
import javafx.scene.canvas.GraphicsContext;
import javafx.scene.paint.Color;

/**
 *  This program draws a checkerboard
 */
public class Checkerboard extends Application {

    /**
     * Draws a picture.  The parameters width and height give the size 
     * of the drawing area, in pixels.  
     */
    public void drawPicture(GraphicsContext g, int width, int height) {

          int row;   // Row number, from 0 to 7
          int col;   // Column number, from 0 to 7
          int x,y;   // Top-left corner of square

          for ( row = 0;  row < 8;  row++ ) {

             for ( col = 0;  col < 8;  col++) {
                x = col * 50;
                y = row * 50;
                if ( (row % 2) == (col % 2) )
                   g.setFill(Color.RED);
                else
                   g.setFill(Color.BLACK);
                g.fillRect(x, y, 50, 50);
             } 

          }

    } // end drawPicture()

    //------ Implementation details: DO NOT EXPECT TO UNDERSTAND THIS ------


    public void start(Stage stage) {
        int width = 400;   // The width of the image.  You can modify this value!
        int height = 400;  // The height of the image. You can modify this value!
        Canvas canvas = new Canvas(width,height);
        drawPicture(canvas.getGraphicsContext2D(), width, height);
        BorderPane root = new BorderPane(canvas);
        root.setStyle("-fx-border-width: 4px; -fx-border-color: #444");
        Scene scene = new Scene(root);
        stage.setScene(scene);
        stage.setTitle("Checkerboard"); // STRING APPEARS IN WINDOW TITLEBAR!
        stage.show();
        stage.setResizable(false);
    } 

    public static void main(String[] args) {
        launch();
    }

} // end Checkerboard

import java.awt.*;
import java.awt.event.*;
import javax.swing.*;

public class Checkerboard extends JPanel implements ActionListener {

   public void drawFrame(Graphics g, int frameNumber, int width, int height) {

      int row;   // Row number, from 0 to 7
      int col;   // Column number, from 0 to 7
      int x,y;   // Top-left corner of square

      for ( row = 0;  row < 8;  row++ ) {

         for ( col = 0;  col < 8;  col++) {
            x = col * 50;
            y = row * 50;
            if ( (row % 2) == (col % 2) )
               g.setColor(Color.RED);
            else
               g.setColor(Color.BLACK);
            g.fillRect(x, y, 50, 50);
         } 

      }

   }

   //------ Implementation details: DO NOT EXPECT TO UNDERSTAND THIS ------


   public static void main(String[] args) {

      JFrame window = new JFrame("Checkerboard");
      Checkerboard drawingArea = new Checkerboard();
      drawingArea.setBackground(Color.WHITE);
      window.setContentPane(drawingArea);
      drawingArea.setPreferredSize(new Dimension(400,400));
      window.pack();
      window.setLocation(100,50);
      window.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
      window.setResizable(false); // The user can't change the size.
      Timer frameTimer = new Timer(20,drawingArea);
      window.setVisible(true);
      //frameTimer.start(); // commented out so we don't get an animation

   } // end main

   private int frameNum;

   public void actionPerformed(ActionEvent evt) {
      frameNum++;
      repaint();
   }

   protected void paintComponent(Graphics g) {
      super.paintComponent(g);
      drawFrame(g, frameNum, getWidth(), getHeight());
   }

}

